mapper.sql:
where tf.delete_flag = 1

    and blog_id=#{blogId}


    and comment_id=#{commentId}


    and floor_id=#{floorId}

mapper.select:
select tf.floor_id AS floorId, tf.comment_id as commentId, tf.blog_id as blogId, tf.floor_comment as floorComment, tf.gmt_modified as gmtCreate, tu.user_id as userId, tu.nick_name as nickName, tu.user_headimg as imgUrl from t_floor_info as tf join t_user tu on tu.user_id = tf.user_id and tu.delete_flag = 1
order by tf.gmt_create desc
limit #{beginIndex},#{everyPage}
